const getCurrentTabStr=()=>`${document.querySelector("ul.nav-tabs>li.active").id.replace("-tab","")}`,periodSelect=document.querySelector("#period-select");periodSelect.addEventListener("change",e=>{document.querySelector("#expenses-search-btn").href=`${location.origin+location.pathname}?period=${e.target.value}&tab=${getCurrentTabStr()}`});const getChangeableLinkBtns=function(){return[document.querySelector("#last-month-btn"),document.querySelector("#next-month-btn"),document.querySelector("#expenses-search-btn")]},setSpecifyTab=()=>{const e=event.target.id.replace("-tab-link","");getChangeableLinkBtns().forEach(t=>{let n=`${t.href.replace(/tab=[a-z]+/,`tab=${e}`)}`;t.href=n})};document.querySelector("#expenses-tab-link").addEventListener("click",setSpecifyTab,!1),document.querySelector("#budgets-tab-link").addEventListener("click",setSpecifyTab,!1);const expensePanelHeading=document.querySelector("#expense-panel-heading"),icon=document.querySelector(".fa-caret-right");expensePanelHeading.addEventListener("click",()=>{icon&&icon.classList.toggle("rotate-arrow")}),window.onload=function(){if(arg.category){const t=arg.category;if("expenses"===arg.tab)var e=document.querySelector(`#expenses-comparison-category-id-${t}`);else e=document.querySelector(`#budgets-comparison-category-id-${t}`);const n=e.getBoundingClientRect();window.scrollBy({top:n.top-30,behavior:"smooth"})}};