window.onload=function(){document.querySelectorAll(".calculator-input-addon").forEach(t=>{t.addEventListener("click",t=>{document.querySelector("#calculator-wrapper").setAttribute("data-calculator-for",t.currentTarget.getAttribute("data-calculator-for"))})}),document.querySelector("#close-calculator-btn").addEventListener("click",()=>{const t=document.querySelector("#calculator-wrapper").getAttribute("data-calculator-for");"mypay-input"!==t&&"partnerpay-input"!==t||$(`#${t}`).change()})};